/* eslint-disable react/prop-types */
import React, { useEffect, useState } from 'react'
import Page from '../../Page';
import useResizeAware from 'react-resize-aware';
import { isMobile } from "react-device-detect";

const Instructions = ({ history }) => {

    const [resizeListener, pageSize] = useResizeAware();
    const [pageWidth, setPageWidth] = useState(null);
    const [pageHeight, setPageHeight] = useState(null);

    useEffect(() => {
        const { width, height } = pageSize;
        setPageHeight(height);
        setPageWidth(width);
    }, [pageSize])

    return (
        <Page background={'#212034'} history={history} showPopup>
            {resizeListener}
            <div className="Instructions">
                { pageWidth && pageHeight &&
                <>  
                    <JumpToPage width={pageWidth} height={pageWidth * .24}/>
                    {/* { isMobile ? <TouchToJump width={pageWidth} height={pageWidth * .24}/> : <ClickToJump width={150} height={200}/>} */}
                    { isMobile ? <NavigateMobile width={280} height={90}/> : <NavigateDesktop width={280} height={90}/> }
             
                    <HelpAndRestart width={pageWidth} height={pageWidth * .35}/>
                </>
                }   
            </div>
        </Page>
    )
}

